//Program to demonstrate Primitive data types and variables Naming Conventions

public class DataTypes {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int intergerValue=1;
		float floatValue = 10.5f;
		double doubleValue = 2.34567543;
		final float FLOATCONSTANT = 23.45f;
		
		System.out.println("intergerValue is of integer dataType whose value is " + intergerValue);
		System.out.println("floatValue is of float dataType whose value is " + floatValue);
		System.out.println("doubleValue is of double dataType whose value is " + doubleValue);
		System.out.println("FLOATCONSTANT is a float constant whose value is " + FLOATCONSTANT);

	}

}
